def selection_sort(arr):
    k = 0
    while k < len(arr):
        z = k + 1
        while z < len(arr):
            if arr[z] < arr[k]:
                temp = arr[k]
                arr[k] = arr[z]
                arr[z] = temp
            z += 1
        k += 1
    return arr

def selection_sort(arr):
    k = 0
    while k <= len(arr) - 1:
        z = k + 1
        while z <= len(arr) - 1:
