        sum_value += arr[k]
        k += 1
    return sum_value'}, {'role': 'assistant', 'content': 'sum_value = sum(arr[0]->arr[k-1])'}])

def selection_sort(arr):
    p = 0
    while p < len(arr):
        z = p + 1
        while z < len(arr):
            if arr[z] < arr[p]:
                temp = arr[p]
                arr[p] = arr[z]
                arr[z] = temp
            z += 1
        p += 1
    return arr

def selection_sort(arr):
    p = 0
