def give_count(arr, target):
    count_value = 0
    j = 0
    while j < len(arr):
        if arr[j] == target:
            count_value += 1
        j += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    j = 0
    while j <= len(arr) - 1:
        if arr[j] == target:
            count_value += 1
        j += 1
    return count_value
