def give_count(arr, target):
    count_value = 0
    o = 0
    while o < len(arr):
        if arr[o] == target:
            count_value += 1
        o += 1
    return count_value

def give_count(arr, target):
    count_value = 0
    o = 0
    while o <= len(arr) - 1:
        if arr[o] == target:
            count_value += 1
        o += 1
    return count_value
