def find_max(arr):
    max_value = arr[0]
    n = 0
    while n < len(arr):
        if arr[n] > max_value:
            max_value = arr[n]
        n += 1
    return max_value

def find_max(arr):
    max_value = arr[0]
    n = -1
    while n < len(arr) - 1:
        n += 1
        if arr[n] > max_value:
            max_value = arr[n]
    return max_value
