def find_min(arr):
    min_value = arr[0]
    b = 0
    while b < len(arr):
        if arr[b] < min_value:
            min_value = arr[b]
        b += 1
    return min_value

def find_min(arr):
    min_value = arr[0]
    b = -1
    while b < len(arr) - 1:
        b += 1
        if arr[b] < min_value:
            min_value = arr[b]
    return min_value
