def find_min(arr):
    min_value = arr[0]
    q = 0
    while q < len(arr):
        if arr[q] < min_value:
            min_value = arr[q]
        q += 1
    return min_value

def find_min(arr):
    min_value = arr[-1]
    q = len(arr)
    while q > 0:
        q -= 1
        if arr[q] < min_value:
            min_value = arr[q]
    return min_value
