def give_id(arr, target):
    id_value = -1
    i = 0
    while i < len(arr):
        if arr[i] == target:
            id_value = i
        i += 1
    return id_value

def give_id(arr, target):
    id_value = -1
    i = len(arr)
    while i > 0:
        i -= 1
        if arr[i] == target:
            id_value = i
    return id_value
