def selection_sort(arr):
    p = 0
    while p < len(arr):
        z = p + 1
        while z < len(arr):
            if arr[z] < arr[p]:
                temp = arr[p]
                arr[p] = arr[z]
                arr[z] = temp
            z += 1
        p += 1
    return arr

def selection_sort(arr):
    p = 0
    while p <= len(arr) - 1:
        z = p + 1
        while z <= len(arr) - 1:
            if arr[z] < arr[p]:
                temp = arr[p]
                arr[p] = arr[z]
                arr[z] = temp
            z += 1
        p += 1
    return arr
