def give_member(arr, id):
    member_value = -1
    k = 0
    while k < len(arr):
        if k == id:
            member_value = arr[k]
        k += 1
    return member_value

def give_member(arr, id):
    member_value = -1
    k = len(arr)
    while k > 0:
        k -= 1
        if k == id:
            member_value = arr[k]
    return member_value
